    private void loginbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginbuttonActionPerformed
        // TODO add your handling code here:
        final String username = "admin";
        final String password = "admin123"; 
        String user = usernametextfield.getText().trim();
        String pass = passwordtextfield.getText().trim();
        if(user.compareTo(username) == 0 && pass.compareTo(password) == 0){
            Homepage home = new Homepage();
            home.setVisible(true);
            this.setVisible(false);
        }
        else
        {
            JOptionPane.showMessageDialog(this,"ERROR:Invalid username or password" );
        }
    }//GEN-LAST:event_loginbuttonActionPerformed

